import { useEffect, useState} from 'react';
import './App.css';
import {onCreateByRoomId} from './graphql/subscriptions'

import {API} from 'aws-amplify'


function App() {
  const [message, updateMessage] = useState(null)
  useEffect(() => {
    subscribe()
  }, [])
  function subscribe(){
    API.graphql({
      query:onCreateByRoomId,
      variables: {
        roomId: "202"
      }
    })
    .subscribe({
      next: messageData => {
        console.log("message: ", messageData.value.data.onCreateByRoomId.message)
        updateMessage(messageData.value.data.onCreateByRoomId.message)
      }   
    })
  }
  return (
    <div className="App">
      <div>
        <h1>{message}</h1>
      </div>
    </div>
  );
}

export default App;
